            Definitions.CHECKSTYLE_BUNDLE, UNABLE_TO_INSTANTIATE_EXCEPTION_MESSAGE,
            new String[] {name, attemptedNames}, null, getClass(), null);
        throw new CheckstyleException(exceptionMessage.getViolation());
    }
    return instance;
}

